    private static readonly System.Drawing.Color WhiteStandartColor1 = System.Drawing.ColorTranslator.FromHtml("#d1ccad");
    private static readonly System.Drawing.Color WhiteStandartColor2 = System.Drawing.ColorTranslator.FromHtml("#fdf7d6");

    private static readonly System.Drawing.Color BlackStandartColor1 = System.Drawing.ColorTranslator.FromHtml("#43444a");

    private StandartSvgShapes(ISvgBaseShapeParams svgParams) : base(svgParams)
    {

    }

    public static StandartSvgShapes Create(ISvgBaseShapeParams svgParams)
        => new(svgParams);

    protected override void DrawKing() => DrawUniverseStandardShape(KingFillData, KingBorderData);

    protected override void DrawQueen() => DrawUniverseStandardShape(QueenFillData, QueenBorderData);

    protected override void DrawRook() => DrawUniverseStandardShape(RookFillData, RookBorderData);

    protected override void DrawBishop() => DrawUniverseStandardShape(BishopFillData, BishopBorderData);

    protected override void DrawKnight() => DrawUniverseStandardShape(KnightFillData, KnightBorderData);

    protected override void DrawPawn() => DrawUniverseStandardShape(PawnFillData, PawnBorderData);
    
    private void DrawUniverseStandardShape(string shapeFillData, string shapeBorderData)
    {
        // градиент и его цвета
        SvgLinearGradientServer lg = new()
        {
            ID = IdColorGradientString,
            GradientUnits = SvgCoordinateUnits.UserSpaceOnUse,
            X1 = 23.33f,
            Y1 = 52.5f,
            X2 = 76.67f,
            Y2 = 52.5f
        };

        foreach (var kit in GetColorsInStandartKit())
        {
            lg.Children.Add(new SvgGradientStop()
            {
                Offset = kit.Value,
                StopColor = new SvgColourServer(kit.Key)
            });
        }

        // пути
        var converter = TypeDescriptor.GetConverter(typeof(SvgPathSegmentList)); //converting path data string to svg

        SvgPath svgPath = new()
        {
            Fill = new SvgDeferredPaintServer($"url(#{IdColorGradientString})"),
            PathData = (SvgPathSegmentList)converter.ConvertFrom(shapeFillData)!
        };

        SvgPath svgPath2 = new()
        {
            PathData = (SvgPathSegmentList)converter.ConvertFrom(shapeBorderData)!
        };

        // вставляю в svg
        pieceSvg.Children.Add(lg);
        pieceSvg.Children.Add(svgPath);
        pieceSvg.Children.Add(svgPath2);
    }
